import {createEndpoint, getBodyAsJson} from '@oracle-cx-commerce/endpoints/factory';
import {populateError} from '@oracle-cx-commerce/endpoints/utils';

export const processInput = payload => {
  //memberId is a required property in request payload.
  console.assert(payload.memberId, 'Missing argument "memberId" for endpoint "addOrganizationMemberRoles"');
  //body includes passing roles array which are to be assigned to member.
  const {memberId, ...body} = payload;

  return {
    params: [memberId],
    body
  };
};

export const processOutput = async response => {
  const json = await getBodyAsJson(response);

  return response.ok ? {json} : populateError(response, json);
};

export default createEndpoint('addMemberRoles', {
  processInput,
  processOutput
});
