import {createEndpoint, getBodyAsJson} from '@oracle-cx-commerce/endpoints/factory';
import {populateError} from '@oracle-cx-commerce/endpoints/utils';
import {transformMember} from '@oracle-cx-commerce/endpoints/utils/transform';

export const processInput = payload => {
  console.assert(payload.memberId, 'Missing argument "memberId" for endpoint "getOrganizationMember"');

  const {memberId, ...rest} = payload;

  return {
    params: [memberId],
    query: {...rest}
  };
};

export const processOutput = async response => {
  const json = await getBodyAsJson(response);

  return response.ok ? transformMember(json) : populateError(response, json);
};

export default createEndpoint('getMember', {
  processInput,
  processOutput
});
