import {createEndpoint, getBodyAsJson} from '@oracle-cx-commerce/endpoints/factory';
import {populateError} from '@oracle-cx-commerce/endpoints/utils';
import {transformMembers} from '@oracle-cx-commerce/endpoints/utils/transform';

export const processInput = ({searchCriteria, ...rest}) => {
  return {
    query: {
      q: searchCriteria,
      ...rest
    }
  };
};

export const processOutput = async response => {
  const json = await getBodyAsJson(response);
  const delta = transformMembers(json);
  delta.json = json;

  return response.ok ? delta : populateError(response, json);
};

export default createEndpoint('listMembers', {
  processInput,
  processOutput
});
