import {createEndpoint, getBodyAsJson} from '@oracle-cx-commerce/endpoints/factory';
import {populateError} from '@oracle-cx-commerce/endpoints/utils';

export const processInput = payload => {
  console.assert(
    payload.memberIds !== undefined,
    'Missing argument "members" for endpoint "removeOrganizationMembers"'
  );

  return {
    body: {
      members: payload.memberIds
    }
  };
};

export const processOutput = async response => {
  const json = await getBodyAsJson(response);

  return response.ok ? {} : populateError(response, json);
};

export default createEndpoint('removeContacts', {
  processInput,
  processOutput
});
