import {createEndpoint, getBodyAsJson} from '@oracle-cx-commerce/endpoints/factory';
import {populateError} from '@oracle-cx-commerce/endpoints/utils';
import {transformOrganization} from '@oracle-cx-commerce/endpoints/utils/transform';

export default createEndpoint('getOrganization', {
  processInput(payload) {
    console.assert(payload.organizationId !== undefined, 'getOrganization requires an organizationId.');

    return {
      params: [payload.organizationId]
    };
  },

  async processOutput(response) {
    const json = await getBodyAsJson(response);

    return response.ok ? transformOrganization(json) : populateError(response, json);
  }
});
