import {createEndpoint, getBodyAsJson} from '@oracle-cx-commerce/endpoints/factory';
import {populateError} from '@oracle-cx-commerce/endpoints/utils';
import {transformCardTypes} from '@oracle-cx-commerce/endpoints/utils/transform';

/**
 * Endpoint adapter for the 'getPaymentTypes' action.
 * This maps the action to an 'getPaymentTypes' endpoint invocation.
 */
export default createEndpoint('getPaymentTypes', {
  /**
   * Convert the response from the endpoint invocation into an object
   * to be merged into the application state.
   */
  async processOutput(response) {
    const json = await getBodyAsJson(response);
    const {items} = json;

    return response.ok ? transformCardTypes(items) : populateError(response, json);
  }
});
