import {createEndpoint, getBodyAsJson} from '@oracle-cx-commerce/endpoints/factory';
import {populateError} from '@oracle-cx-commerce/endpoints/utils';
import {transformProduct} from '@oracle-cx-commerce/endpoints/utils/transform';

export const processInput = payload => {
  const {productId, _filterKey: filterKey = 'OSFProduct', ...queryParams} = payload;
  console.assert(productId !== undefined, 'getProduct requires an productId.');

  const query = {
    ...queryParams
  };

  if (filterKey !== '') {
    query.filterKey = filterKey;
  }

  return {
    params: [productId],
    query
  };
};

export const processOutput = async response => {
  const product = await getBodyAsJson(response);

  if (!response.ok) {
    return populateError(response, product);
  }

  return {...transformProduct(product)};
};

export default createEndpoint('getProduct', {
  processInput,
  processOutput
});
