import {createEndpoint, getBodyAsJson} from '@oracle-cx-commerce/endpoints/factory';
import {transformStockStatusResponse} from '@oracle-cx-commerce/endpoints/utils/transform';
import {populateError} from '@oracle-cx-commerce/endpoints/utils';

export const processInput = payload => {
  console.assert(payload.products, 'getStockStatus requires an array of product IDs in a property named `products`.');

  return {query: payload};
};

export const processOutput = async response => {
  const json = await getBodyAsJson(response);

  return response.ok ? {...transformStockStatusResponse(json.items), json} : populateError(response, json);
};

export default createEndpoint('getStockStatuses', {
  processInput,
  processOutput
});
