import {createEndpoint, getBodyAsJson} from '@oracle-cx-commerce/endpoints/factory';
import {getUpdatedAddress, populateError} from '@oracle-cx-commerce/endpoints/utils';
import {transformNewProfileAddress} from '@oracle-cx-commerce/endpoints/utils/transform';

export const processInput = payload => {
  const newAddress = getUpdatedAddress(payload);
  console.assert(payload.firstName !== undefined, `Missing argument address in createProfileAddress`);
  console.assert(payload.lastName !== undefined, `Missing argument address in createProfileAddress`);
  console.assert(payload.country !== undefined, `Missing argument address in createProfileAddress`);
  console.assert(payload.state !== undefined, `Missing argument address in createProfileAddress`);
  console.assert(payload.postalCode !== undefined, `Missing argument address in createProfileAddress`);
  console.assert(payload.city !== undefined, `Missing argument address in createProfileAddress`);
  console.assert(payload.address1 !== undefined, `Missing argument address in createProfileAddress`);
  const {addressType, ...address} = newAddress;

  return {
    body: {
      addressType,
      address
    }
  };
};

export const processOutput = async (response, state) => {
  const json = await getBodyAsJson(response);

  return response.ok ? transformNewProfileAddress(json, state) : populateError(response, json);
};

export default createEndpoint('addProfileAddress', {
  processInput,
  processOutput
});
