import {createEndpoint, getBodyAsJson} from '@oracle-cx-commerce/endpoints/factory';
import {populateError} from '@oracle-cx-commerce/endpoints/utils';
import {deleteAddressFromProfile} from '@oracle-cx-commerce/endpoints/utils/transform';

export const processInput = payload => {
  console.assert(payload.addressId, 'Missing argument addressId for endpoint deleteProfileAddress');

  const {addressId} = payload;

  return {
    params: [addressId]
  };
};

export const processOutput = async (response, state) => {
  const json = await getBodyAsJson(response);

  return response.ok ? deleteAddressFromProfile(json, state) : populateError(response, json);
};

export default createEndpoint('deleteProfileAddress', {
  processInput,
  processOutput
});
