import {createEndpoint, getBodyAsJson} from '@oracle-cx-commerce/endpoints/factory';
import {populateError} from '@oracle-cx-commerce/endpoints/utils';
import {transformProfileAddress} from '@oracle-cx-commerce/endpoints/utils/transform';

export const processInput = payload => {
  const {addressId} = payload;
  console.assert(addressId !== undefined, `Missing argument addressId in getProfileAddress`);

  return {
    params: [addressId]
  };
};

export const processOutput = async response => {
  const json = await getBodyAsJson(response);

  return response.ok ? transformProfileAddress(json) : populateError(response, json);
};

export default createEndpoint('getProfileAddress', {
  processInput,
  processOutput
});
