import {createEndpoint, getBodyAsJson} from '@oracle-cx-commerce/endpoints/factory';

import {populateError} from '@oracle-cx-commerce/endpoints/utils';
import {transformProfileAddresses} from '@oracle-cx-commerce/endpoints/utils/transform';

export const processInput = ({query, ...rest}) => {
  return {
    query: {
      q: query,
      ...rest
    }
  };
};

export const processOutput = async response => {
  const json = await getBodyAsJson(response);
  if (response.ok) {
    const addressesMap = transformProfileAddresses(json.items);

    return {
      profileRepository: {
        contactInfos: {
          ...addressesMap
        }
      },
      json
    };
  }

  return populateError(response, json);
};

export default createEndpoint('listProfileAddresses', {
  processInput,
  processOutput
});
