import {createEndpoint, getBodyAsJson} from '@oracle-cx-commerce/endpoints/factory';
import {populateError, updateDefaultShippingAddress} from '@oracle-cx-commerce/endpoints/utils';
import {transformMarkDefaultShippingAddress} from '@oracle-cx-commerce/endpoints/utils/transform';

export const processInput = (payload, state) => {
  const {addressId} = payload;
  console.assert(addressId !== undefined, `Missing argument addressId in updateDefaultProfileAddress`);

  return {
    params: [addressId],
    body: updateDefaultShippingAddress(addressId, state)
  };
};

export const processOutput = async (response, state) => {
  const json = await getBodyAsJson(response);

  return response.ok ? transformMarkDefaultShippingAddress(json, state) : populateError(response, json);
};

export default createEndpoint('updateProfileAddress', {
  processInput,
  processOutput
});
