import {createEndpoint, getBodyAsJson} from '@oracle-cx-commerce/endpoints/factory';
import {getUpdatedAddress, populateError} from '@oracle-cx-commerce/endpoints/utils';
import {transformAddressAndUpdateProfileDefaultAddress} from '@oracle-cx-commerce/endpoints/utils/transform';

export const processInput = payload => {
  const newAddress = getUpdatedAddress(payload);
  const {addressType, addressId, ...address} = newAddress;
  console.assert(addressId !== undefined, `Missing argument addressId in updateProfileAddress`);
  console.assert(address !== undefined, `Missing argument address in updateProfileAddress`);

  return {
    params: [addressId],
    body: {
      addressType,
      address
    }
  };
};

export const processOutput = async (response, state) => {
  const json = await getBodyAsJson(response);

  return response.ok ? transformAddressAndUpdateProfileDefaultAddress(json, state) : populateError(response, json);
};

export default createEndpoint('updateProfileAddress', {
  processInput,
  processOutput
});
