import {createEndpoint, getBodyAsJson} from '@oracle-cx-commerce/endpoints/factory';
import {getNewProfile, populateError} from '@oracle-cx-commerce/endpoints/utils';
import {updateProfileRepositories} from '@oracle-cx-commerce/endpoints/utils/transform';

export const processInput = payload => {
  return {body: getNewProfile(payload)};
};

export const processOutput = async response => {
  const json = await getBodyAsJson(response);

  return response.ok ? updateProfileRepositories(json) : populateError(response, json);
};

export default createEndpoint('updateProfile', {
  processInput,
  processOutput
});
