import {createEndpoint, getBodyAsJson} from '@oracle-cx-commerce/endpoints/factory';
import {mapSitePropertiesResponse, populateError} from '@oracle-cx-commerce/endpoints/utils';

export const processInput = payload => {
  return {body: {siteProperties: [...payload]}};
};

export const processOutput = async (response, state) => {
  const json = await getBodyAsJson(response);

  return response.ok ? mapSitePropertiesResponse(state, json) : populateError(response, json);
};

export default createEndpoint('updateSiteProperties', {
  processInput,
  processOutput
});
