import {createEndpoint, getBodyAsJson} from '@oracle-cx-commerce/endpoints/factory';

import {populateError} from '@oracle-cx-commerce/endpoints/utils';

export const processInput = payload => {
  console.assert(payload.purchaseListId !== undefined, 'checkPurchaseListEditAccess requires a purchaseListId.');

  return {
    body: payload
  };
};

export const processOutput = async (response, state, inputPayload) => {
  const jsonResponse = await getBodyAsJson(response);

  const {purchaseListId} = inputPayload;

  return response.ok ? {json: {...jsonResponse, id: purchaseListId}} : populateError(response, jsonResponse);
};

export default createEndpoint('checkEditAccess', {processInput, processOutput});
