import {createEndpoint, getBodyAsJson} from '@oracle-cx-commerce/endpoints/factory';

import {populateError} from '@oracle-cx-commerce/endpoints/utils';
import {transformPurchaseList} from '@oracle-cx-commerce/endpoints/utils/transform';

export const processInput = payload => {
  const {purchaseListId, ...restBodyPayload} = payload;
  console.assert(purchaseListId, 'update purchase list requires purchaseListId');

  return {
    params: [purchaseListId],
    body: restBodyPayload
  };
};

export const processOutput = async response => {
  const json = await getBodyAsJson(response);

  return response.ok ? transformPurchaseList(json) : populateError(response, json);
};

export default createEndpoint('updatePurchaseList', {processInput, processOutput});
