import {createEndpoint, getBodyAsJson} from '@oracle-cx-commerce/endpoints/factory';
import {
  mergeExcludeParams,
  transformCheckoutOrder,
  updateRequestContext
} from '@oracle-cx-commerce/endpoints/utils/transform';
import {populateError} from '@oracle-cx-commerce/endpoints/utils';
import {ORDER_STATE_QUOTED} from '@oracle-cx-commerce/commerce-utils/constants';

const processInput = payload => {
  console.assert(payload.orderId !== undefined, "submitQuotedOrder requires an order's Id.");
  const {queryParams, orderId, ...payloadBody} = payload;
  const excludeParams = mergeExcludeParams(queryParams, 'getOrder');

  return {
    params: [orderId],
    body: payloadBody,
    query: {...queryParams, exclude: excludeParams}
  };
};

const processOutput = async response => {
  const json = await getBodyAsJson(response);
  const order = response.ok ? transformCheckoutOrder(json) : populateError(response, json);

  return response.ok && json && json.state !== ORDER_STATE_QUOTED
    ? {
        ...order,
        ...updateRequestContext(json)
      }
    : order;
};
export default createEndpoint('submitQuotedOrder', {processInput, processOutput});
