import {createEndpoint, getBodyAsJson} from '@oracle-cx-commerce/endpoints/factory';
import {mergeExcludeParams, transformCheckoutOrder} from '@oracle-cx-commerce/endpoints/utils/transform';
import {populateError} from '@oracle-cx-commerce/endpoints/utils';

const processInput = payload => {
  console.assert(payload.orderId !== undefined, "updateQuotedOrder requires an order's id.");
  const {queryParams, orderId, ...payloadBody} = payload;
  const excludeParams = mergeExcludeParams(queryParams, 'getOrder');

  return {
    params: [orderId],
    body: payloadBody,
    query: {...queryParams, exclude: excludeParams}
  };
};

const processOutput = async response => {
  const json = await getBodyAsJson(response);
  const order = response.ok ? transformCheckoutOrder(json) : populateError(response, json);

  return order;
};
export default createEndpoint('updateQuotedOrder', {processInput, processOutput});
