import {createEndpoint, getBodyAsJson} from '@oracle-cx-commerce/endpoints/factory';
import {populateError} from '@oracle-cx-commerce/endpoints/utils';
import {transformContactRequest} from '@oracle-cx-commerce/endpoints/utils/transform/registration-request';

export const processInput = payload => {
  console.assert(payload.contactRequestId !== undefined, 'rejectProfileRequest requires contactRequestId.');

  const {contactRequestId, ...payloadBody} = payload;

  return {
    params: [contactRequestId],
    body: payloadBody
  };
};

export const processOutput = async response => {
  const json = await getBodyAsJson(response);
  const {links, ...result} = json;
  if (!response.ok) {
    return populateError(response, json);
  }

  if (result.id && result.status) {
    result.profile = null;
  }

  return transformContactRequest(result);
};

export default createEndpoint('rejectProfileRequest', {
  processInput,
  processOutput
});
