import {createEndpoint, getBodyAsJson} from '@oracle-cx-commerce/endpoints/factory';
import {populateCommonResources} from '@oracle-cx-commerce/endpoints/utils/transform';
import {populateError} from '@oracle-cx-commerce/endpoints/utils';

export const processInput = payload => {
  const {queryParams} = payload;

  return {
    params: ['ns.common'],
    query: {...queryParams}
  };
};

export const processOutput = async response => {
  const json = await getBodyAsJson(response);
  delete json.links;

  return response.ok ? populateCommonResources(json) : populateError(response, json);
};

export default createEndpoint('getResourceBundle', {
  processInput,
  processOutput
});
