import {createEndpoint, getBodyAsJson} from '@oracle-cx-commerce/endpoints/factory';
import {populateError} from '@oracle-cx-commerce/endpoints/utils';

export const processInput = payload => {
  console.assert(payload.orderId !== undefined, 'calculate refund requires an order ID.');
  console.assert(payload.returnItems !== undefined, 'calculate refund requires atleast one return item');

  return {
    body: payload
  };
};

export const processOutput = async response => {
  const returnRequest = await getBodyAsJson(response);

  return response.ok ? {json: returnRequest} : populateError(response, returnRequest);
};

export default createEndpoint('calculateRefund', {
  processInput,
  processOutput
});
