import {createEndpoint, getBodyAsJson} from '@oracle-cx-commerce/endpoints/factory';
import {populateError} from '@oracle-cx-commerce/endpoints/utils';
import {populateReturnRequest} from '@oracle-cx-commerce/endpoints/utils/transform';

export const processInput = payload => {
  const {orderId, returnItems} = payload;

  return {
    body: {
      orderId,
      returnItems
    }
  };
};

export const processOutput = async response => {
  const returnResponse = await getBodyAsJson(response);

  if (!response.ok) {
    return populateError(response, returnResponse);
  }

  return populateReturnRequest(returnResponse);
};

export default createEndpoint('createReturnRequest', {
  processInput,
  processOutput
});
