import {createEndpoint, getBodyAsJson} from '@oracle-cx-commerce/endpoints/factory';
import {addReturnRequestDetails} from '@oracle-cx-commerce/endpoints/utils/transform';
import {populateError} from '@oracle-cx-commerce/endpoints/utils';

export const processInput = payload => {
  console.assert(payload.id !== undefined, 'returnRequest requires a returnRequest ID.');
  const {queryParams} = payload;
  const returnRequestId = payload.id;

  return {
    params: [returnRequestId],
    query: {...queryParams}
  };
};

export const processOutput = async (response, state, {id}) => {
  // should the id be changed to returnRequestId?
  const returnRequest = await getBodyAsJson(response);

  return response.ok ? addReturnRequestDetails(returnRequest, id) : populateError(response, returnRequest);
};

export default createEndpoint('getReturnRequest', {
  processInput,
  processOutput
});
