import {createEndpoint, getBodyAsJson} from '@oracle-cx-commerce/endpoints/factory';
import {addReturnsSummary} from '@oracle-cx-commerce/endpoints/utils/transform';
import {populateError} from '@oracle-cx-commerce/endpoints/utils';

export const processInput = payload => {
  console.assert(payload.id !== undefined, 'returnRequests requires an order ID.');
  const {queryParams} = payload;
  const orderId = payload.id;

  return {
    params: [orderId],
    query: {...queryParams}
  };
};

export const processOutput = async (response, state, {id}) => {
  const returnRequests = await getBodyAsJson(response);

  return response.ok ? addReturnsSummary(returnRequests, id) : populateError(response, returnRequests);
};

export default createEndpoint('listReturnRequests', {
  processInput,
  processOutput
});
