import {createEndpoint, getBodyAsJson} from '@oracle-cx-commerce/endpoints/factory';
import {
  transformCurrentOrderToScheduledOrder,
  updateSessionOnOrderSubmission
} from '@oracle-cx-commerce/endpoints/utils/transform';
import {populateError} from '@oracle-cx-commerce/endpoints/utils';

const processInput = payload => {
  return {
    body: payload
  };
};

const processOutput = async response => {
  const json = await getBodyAsJson(response);

  const order = response.ok ? transformCurrentOrderToScheduledOrder(json) : populateError(response, json);

  return response.ok && json && json.state !== 'INCOMPLETE'
    ? {
        ...order,
        ...updateSessionOnOrderSubmission(json)
      }
    : order;
};
export default createEndpoint('convertToScheduledOrder', {processInput, processOutput});
