import {createEndpoint, getBodyAsJson} from '@oracle-cx-commerce/endpoints/factory';
import {transformScheduledOrder} from '@oracle-cx-commerce/endpoints/utils/transform';
import {populateError} from '@oracle-cx-commerce/endpoints/utils';

export const processInput = payload => {
  console.assert(payload.scheduledOrderId !== undefined, 'getScheduledOrder requires an scheduledOrderId.');

  const {scheduledOrderId, ...queryParams} = payload;

  return {
    params: [scheduledOrderId],
    query: queryParams
  };
};

export const processOutput = async response => {
  const json = await getBodyAsJson(response);

  return response.ok ? transformScheduledOrder(json) : populateError(response, json);
};

export default createEndpoint('getScheduledOrder', {
  processInput,
  processOutput
});
