import {createEndpoint, getBodyAsJson} from '@oracle-cx-commerce/endpoints/factory';
import {populateError} from '@oracle-cx-commerce/endpoints/utils';
import {transformExecutionStatus} from '@oracle-cx-commerce/endpoints/utils/transform';

const processInput = payload => {
  console.assert(
    payload.scheduledOrderId !== undefined,
    'listExecutionStatusForScheduledOrderById requires an scheduledOrderId.'
  );
  const {scheduledOrderId, ...queryParams} = payload;

  return {
    params: [scheduledOrderId],
    query: queryParams
  };
};

const processOutput = async response => {
  const json = await getBodyAsJson(response);
  if (!response.ok) {
    return populateError(response, json);
  }
  const itemsObject = transformExecutionStatus(json.items);

  return {
    ...itemsObject,
    json
  };
};
export default createEndpoint('listExecutionStatusForScheduledOrderById', {processInput, processOutput});
