import {createEndpoint, getBodyAsJson} from '@oracle-cx-commerce/endpoints/factory';
import {populateError} from '@oracle-cx-commerce/endpoints/utils';
import {transformScheduledOrders} from '@oracle-cx-commerce/endpoints/utils/transform';

const processInput = payload => {
  return {
    query: payload
  };
};

const processOutput = async response => {
  const json = await getBodyAsJson(response);
  if (!response.ok) {
    return populateError(response, json);
  }
  const itemsObject = transformScheduledOrders(json.items);

  return {
    ...itemsObject,
    json
  };
};
export default createEndpoint('listScheduledOrdersByProfile', {processInput, processOutput});
