import {createEndpoint, getBodyAsJson} from '@oracle-cx-commerce/endpoints/factory';
import {populateError} from '@oracle-cx-commerce/endpoints/utils';

export const processInput = () => {
  return {query: {encode: true}};
};

export const processOutput = async response => {
  const json = await getBodyAsJson(response);
  const {authnRequestTarget, authnRequest} = json;

  return response.ok
    ? {
        clientRepository: {
          context: {
            session: {
              authnRequest,
              authnRequestTarget
            }
          }
        }
      }
    : populateError(response, json);
};

export default createEndpoint('samlAuthnRequest', {
  processInput,
  processOutput
});
