import {createEndpoint, getBodyAsJson} from '@oracle-cx-commerce/endpoints/factory';
import {populateError} from '@oracle-cx-commerce/endpoints/utils';
import {transformShareSetting} from '@oracle-cx-commerce/endpoints/utils/transform';

export const processInput = payload => {
  console.assert(payload.shareSettingId !== undefined, 'Missing argument shareSettingId in getShareSetting');

  return {
    params: [payload.shareSettingId]
  };
};

export const processOutput = async response => {
  const json = await getBodyAsJson(response);

  return response.ok ? transformShareSetting(json) : populateError(response, json);
};

export default createEndpoint('getShareSetting', {processInput, processOutput});
