import {createEndpoint, getBodyAsJson} from '@oracle-cx-commerce/endpoints/factory';
import {populateError} from '@oracle-cx-commerce/endpoints/utils';
import {transformEmailConfigs} from '@oracle-cx-commerce/endpoints/utils/transform';

export const processInput = payload => {
  console.assert(payload.shareSettingId !== undefined, 'Missing argument shareSettingId in updateEmailConfigs');
  const {shareSettingId, ...updateEmailPayload} = payload;

  return {params: [shareSettingId], body: updateEmailPayload};
};

export const processOutput = async response => {
  const json = await getBodyAsJson(response);

  return response.ok ? transformEmailConfigs(json) : populateError(response, json);
};

export default createEndpoint('updateEmailConfigs', {processInput, processOutput});
