import {createEndpoint, getBodyAsJson} from '@oracle-cx-commerce/endpoints/factory';
import {populateError} from '@oracle-cx-commerce/endpoints/utils';
import {transformShippingCountries} from '@oracle-cx-commerce/endpoints/utils/transform';

export const processInput = ({currentPriceListGroup}) => {
  return {query: {currentPriceListGroup}};
};

export const processOutput = async response => {
  const json = await getBodyAsJson(response);
  const {items} = json;

  return response.ok ? {...transformShippingCountries(items)} : populateError(response, json);
};

export default createEndpoint('getShippingCountries', {
  processInput,
  processOutput
});
