import {createEndpoint, getBodyAsJson} from '@oracle-cx-commerce/endpoints/factory';
import {populateError} from '@oracle-cx-commerce/endpoints/utils';
import {transformShopperContext} from '@oracle-cx-commerce/endpoints/utils/transform';

export const processInput = payload => {
  return {
    body: payload
  };
};

export const processOutput = async response => {
  const json = await getBodyAsJson(response);

  return response.ok ? transformShopperContext(json, response) : populateError(response, json);
};

export default createEndpoint('setShopperContext', {
  processInput,
  processOutput
});
