const express = require('express');

const statusCheckRouter = new express.Router();
statusCheckRouter.get('/status', (req, res /*, next*/) => {
  res.send({deploymentId: process.env.IPC_ID || 'local'});
});

module.exports = () => {
  return {statusCheckRouter};
};
