import app from '@oracle-cx-commerce/react-app/server/middleware';
import {noop} from '@oracle-cx-commerce/utils/generic';

export const createServer = ({middleware = noop, components, endpoints, meta}) => {
  return {
    middleware: [middleware, app],
    components,
    endpoints,
    meta
  };
};
