import getMetaHtml from '@oracle-cx-commerce/react-app/server/middleware/app/html/meta.html';
import getPwaHtml from '@oracle-cx-commerce/react-app/server/middleware/app/html/pwa.html';
import getLinkHtml from '@oracle-cx-commerce/react-app/server/middleware/app/html/link.html';
import getStyleHtml from '@oracle-cx-commerce/react-app/server/middleware/app/html/style.html';
import getScriptHtml from '@oracle-cx-commerce/react-app/server/middleware/app/html/script.html';
import getSeoMetaHtml from '@oracle-cx-commerce/react-app/server/middleware/app/html/seo-meta.html';

export default (req, res) => {
  const metaHtml = getMetaHtml(req, res);
  const pwaHtml = getPwaHtml(req, res);
  const linkHtml = getLinkHtml(req, res);
  const styleHtml = getStyleHtml(req, res);
  const scriptHtml = getScriptHtml(req, res);
  const seoMetaHtml = getSeoMetaHtml(req, res);

  return `${metaHtml}${pwaHtml}${linkHtml}${styleHtml}${scriptHtml}${seoMetaHtml}`;
};
