import React from 'react';
import Styled from '@oracle-cx-commerce/react-components/styled';
import css from '@oracle-cx-commerce/react-components/action-icon/styles.css';

const {Children, createElement, cloneElement} = React;

/**
 * Displays the action icon
 */

const ActionIcon = ({children}) => {
  return createElement(
    Styled,
    {id: 'ActionIcon', css},
    cloneElement(Children.only(children), {className: 'ActionIcon'})
  );
};

export default ActionIcon;
