import React from 'react';
import Styled from '@oracle-cx-commerce/react-components/styled';
import css from '@oracle-cx-commerce/react-components/badge/styles.css';
import PropTypes from 'prop-types';

/**
 * Displays the Badge component
 */

const Badge = ({badgeText = '', className = ''}) => {
  return (
    <Styled css={css} id="Badge">
      <span className={`Badge__Text ${className}`} aria-label={badgeText}>
        {badgeText}
      </span>
    </Styled>
  );
};

Badge.propTypes = {
  /**
   * Text to be shown in the badge
   */
  badgeText: PropTypes.string,

  /**
   * Class name to be applied to the HTML element.
   */
  className: PropTypes.string
};

Badge.defaultProps = {
  badgeText: '',
  className: ''
};

export default React.memo(Badge);
