import React from 'react';
import Styled from '@oracle-cx-commerce/react-components/styled';
import css from '@oracle-cx-commerce/react-components/card/styles.css';
import PropTypes from 'prop-types';

/**
 * Displays the Card component
 */
const Card = props => {
  const {children = '', type = 'secondary', className = '', ...elementAttributes} = props;
  let classList = className;
  if (type.toLowerCase() === 'primary') classList += ' Card__Primary';

  return (
    <Styled css={css} id="Card">
      <div className={`Card ${classList}`} {...elementAttributes}>
        {children}
      </div>
    </Styled>
  );
};

Card.propTypes = {
  /**
   * Class name to be applied to the HTML element.
   */
  className: PropTypes.string,

  /**
   * Type of the card [secondary, primary ]
   */
  type: PropTypes.string,

  /**
   * Child nodes to by displayed
   */
  children: PropTypes.oneOfType([PropTypes.arrayOf(PropTypes.node), PropTypes.node])
};

Card.defaultProps = {
  children: '',
  type: 'secondary',
  className: ''
};

export default React.memo(Card);
