import React from 'react';

import Styled from '@oracle-cx-commerce/react-components/styled';
import css from '@oracle-cx-commerce/react-components/checkbox/styles.css';
import PropTypes from 'prop-types';
/**
 * Displays a checkbox
 */
const Checkbox = props => {
  const {className = '', labelText = '', id, ...elementAttributes} = props;
  delete elementAttributes.type;

  return (
    <Styled css={css} id="Checkbox">
      <span className={`Checkbox ${className}`}>
        <label htmlFor={`${id ? id : ''}${elementAttributes.name ? elementAttributes.name : ''}`}>
          <input
            type="checkbox"
            {...elementAttributes}
            id={`${id ? id : ''}${elementAttributes.name ? elementAttributes.name : ''}`}
          />
          <span className="Checkbox__Control"></span>
          <span className="Checkbox__Label">{labelText}</span>
        </label>
      </span>
    </Styled>
  );
};

Checkbox.propTypes = {
  /**
   * ID of select element.
   */
  id: PropTypes.string,

  /**
   * Class name to be applied to the HTML Select element.
   */
  className: PropTypes.string,

  /**
   * Text of the checkbox label
   */
  labelText: PropTypes.string
};

Checkbox.defaultProps = {
  id: undefined,
  className: '',
  labelText: ''
};

export default React.memo(Checkbox);
