import React from 'react';
import Styled from '@oracle-cx-commerce/react-components/styled';
import css from '@oracle-cx-commerce/react-components/icons/styles.css';
import PropTypes from 'prop-types';

/**
 * Displays the Icons
 */
const Icon = ({children, className = '', title, ...rest}) => (
  <Styled id="Icon" css={css}>
    <svg className={`Icon ${className}`.trim()} viewBox="0 0 24 24" {...rest}>
      {title && <title>{title}</title>}
      {children}
    </svg>
  </Styled>
);

Icon.propTypes = {
  /**
   * Child nodes to by displayed
   */
  children: PropTypes.oneOfType([PropTypes.arrayOf(PropTypes.node), PropTypes.node]).isRequired,

  /**
   * Class name to be applied to the HTML element.
   */
  className: PropTypes.string,

  /**
   * Title of the icon
   */
  title: PropTypes.string
};

Icon.defaultProps = {
  className: '',
  title: undefined
};

export default Icon;
