import React from 'react';

import Styled from '@oracle-cx-commerce/react-components/styled';
import css from '@oracle-cx-commerce/react-components/radio/styles.css';
import PropTypes from 'prop-types';

/**
 * Displays a radio button
 */

const Radio = props => {
  const {className = '', labelText = '', id, optionInputRef = null, ...elementAttributes} = props;
  delete elementAttributes.type;

  return (
    <Styled id="Radio" css={css}>
      <span className={`Radio ${className}`}>
        <label htmlFor={`${id ? id : ''}${elementAttributes.name ? elementAttributes.name : ''}`}>
          <input
            type="radio"
            {...elementAttributes}
            ref={optionInputRef}
            id={`${id ? id : ''}${elementAttributes.name ? elementAttributes.name : ''}`}
          ></input>
          <span className="Radio__Control"></span>
          <span className="Radio__Label">{labelText}</span>
        </label>
      </span>
    </Styled>
  );
};

Radio.propTypes = {
  /**
   * Class name to be applied to the HTML element.
   */
  className: PropTypes.string,

  /**
   * ID of the element
   */
  id: PropTypes.string,

  /**
   * Text of the button label
   */
  labelText: PropTypes.oneOfType([PropTypes.string, PropTypes.object])
};

Radio.defaultProps = {
  id: undefined,
  className: '',
  labelText: ''
};

export default React.memo(Radio);
