import {connect} from '@oracle-cx-commerce/react-components/provider';
import {getCurrentOrder} from '@oracle-cx-commerce/commerce-utils/selector';

export const cart = WrappedComponent => connect(getCurrentOrder)(WrappedComponent);

const Cart = ({children, ...props}) => (typeof children === 'function' ? children(props) : children);

export default cart(Cart);
