import {connect} from '@oracle-cx-commerce/react-components/provider';
import {getOrder} from '@oracle-cx-commerce/commerce-utils/selector';

export const order = WrappedComponent => connect(getOrder)(WrappedComponent);

const Order = ({children, ...props}) => (typeof children === 'function' ? children(props) : children);

export default order(Order);
