import {connect} from '@oracle-cx-commerce/react-components/provider';
import {getProduct} from '@oracle-cx-commerce/commerce-utils/selector';

export const product = WrappedComponent => connect(getProduct)(WrappedComponent);

const Product = ({children, ...props}) => (typeof children === 'function' ? children(props) : children);

export default product(Product);
