import {connect} from '@oracle-cx-commerce/react-components/provider';
import {getCurrentProfile} from '@oracle-cx-commerce/commerce-utils/selector';

export const profile = WrappedComponent => connect(getCurrentProfile)(WrappedComponent);

const Profile = ({children, ...props}) => (typeof children === 'function' ? children(props) : children);

export default profile(Profile);
