import {connect} from '@oracle-cx-commerce/react-components/provider';
import {getSku} from '@oracle-cx-commerce/commerce-utils/selector';

export const sku = WrappedComponent => connect(getSku)(WrappedComponent);

const Sku = ({children, ...props}) => (typeof children === 'function' ? children(props) : children);

export default sku(Sku);
