import React from 'react';

const Fetcher = props => {
  const {endpoints = [], endpointsSubCategoryMap, basePageURL} = props;

  return (
    <div className="sub-block">
      <div className="sub-details">
        <div className="schema-intro-details">
          <h2>Endpoints</h2>
          {endpoints.length ? (
            <>
              <ul>
                {endpoints.map(endpoint => (
                  <li key={endpoint}>
                    {endpointsSubCategoryMap.get(endpoint) ? (
                      <a href={`${basePageURL}#endpoints/${endpointsSubCategoryMap.get(endpoint)}/${endpoint}`}>
                        {endpoint}
                      </a>
                    ) : (
                      <span>{endpoint}</span>
                    )}
                  </li>
                ))}
              </ul>
            </>
          ) : (
            ''
          )}
        </div>
      </div>
    </div>
  );
};
export default Fetcher;
